package BaiTap2109;

public class BT1_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("Average speed in miles is ");
		// 30 giây = 0.5 phút
		// t(phút) = 50.5 (phút)
		// t(h) = 50.5 / 60 (h)
		// s(km) = 15 (km)
		// s(dặm) = 15 / 1.6 (dặm)
		// v(dặm/h) = s / t(dặm/h)
		System.out.println((15/1.6)/(50.5/60));
	}

}
